var Login = function() {
    var e = function() {
            $(".login-form").validate({
                errorElement: "span",
                errorClass: "help-block",
                focusInvalid: !1,
                rules: {
                    UserName: {
                        required: !0
                    },
                    Password: {
                        required: !0
                    },
                    LoginCode: {
                       required: !0
                    }
                },
                messages: {
                    UserName: {
                        required: "User Name is required."
                    },
                    Password: {
                        required: "Password is required."
                    },
					LoginCode: {
                        required: "LoginCode is required."
                    }
                },
                invalidHandler: function(e, r) {
					//$(".alert-danger", $(".login-form")).show()
                },
                highlight: function(e) {
                    $(e).closest(".form-group").addClass("has-error")
                },
                success: function(e) {
                    e.closest(".form-group").removeClass("has-error"), e.remove()
                },
                errorPlacement: function(e, r) {
                   e.insertAfter(r.closest(".input-icon"))
                },
                submitHandler: function(e) {
					e.submit()
                }
            }), $(".login-form input").keypress(function(e) {
                if (13 == e.which) return $(".login-form").validate().form() && $(".login-form").submit(), !1
            })
        },
        r = function() {
            $(".forget-form").validate({
                errorElement: "span",
                errorClass: "help-block",
                focusInvalid: !1,
                ignore: "",
                rules: {
                    email: {
                        required: !0,
                        email: !0
                    }
                },
                messages: {
                    email: {
                        required: "Email is required."
                    }
                },
                invalidHandler: function(e, r) {},
                highlight: function(e) {
                    $(e).closest(".form-group").addClass("has-error")
                },
                success: function(e) {
                    e.closest(".form-group").removeClass("has-error"), e.remove()
                },
                errorPlacement: function(e, r) {
                    var i = $(r).parent(".input-group");
                    i.size() > 0 ? i.after(e) : r.after(e)
                },
                submitHandler: function(e) {
                    e.submit()
                }
            }), $(".forget-form input").keypress(function(e) {
                if (13 == e.which) return $(".forget-form").validate().form() && $(".forget-form").submit(), !1
            }), jQuery("#forget-password").click(function() {
                jQuery(".login-form").hide(), jQuery(".forget-form").show()
            }), jQuery("#back-btn").click(function() {
                jQuery(".login-form").show(), jQuery(".forget-form").hide()
            })
        };
    return {
        init: function() {
            e(), r()
        }
    }
}();
jQuery(document).ready(function() {
    Login.init()
});